import "./form-input.styles.scss";

const FormInput = ({ label, value, name, type, onChangeHandler }) => {
  return (
    <>
      <label>{label}</label>
      <input
        type={type}
        required
        onChange={onChangeHandler}
        name={name}
        value={value}
      ></input>
    </>
  );
};

export default FormInput;
